table.map:
function(doc){
  
  var columnNames = [];
  var values = [];
  var map = [];
  
    if(doc.source == "Azure DevOps") {
      var index = 0;
      for(o in doc.data.value) {
        buildMap("", doc.data.value[o], index++);
      }
        
      for(i in map) {
        columnNames.push(map[i][0]);
        map[i].shift();
        values.push(map[i]);
      }
      emit([doc.source, doc.project], {"columnNames": columnNames, "values": values});
    }
  
  function add(array, key, value, index) {
    var existed = false;
    for(x in array) {
      if(array[x][0] == key) {
        addNull(array[x], index - (array[x].length - 1));
        array[x].push(value);
        existed = true;
      }
    }
    if(!existed) {
      var entry = [];
      entry.push(key);
      addNull(entry, index)
      entry.push(value);
      array.push(entry);
    }
  }
  
  function addNull(array, n)
  {
    for(var i = 0; i < n; i++) {
        array.push(null);
      }
  }
    
    function buildMap(prefix, o, index) {
      for (tag in o) {
        if(typeof o[tag] == 'object') buildMap(prefix + "." + tag, o[tag], index)
        else {
          var dottedName = prefix + "." + tag;
          var name = dottedName.substring(1, dottedName.length);
          add(map, name, o[tag], index);
        }
      }
    }
}
